import React from 'react'
import Player from '../player'
import AllVideos from '../allVideos'

class Home extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      route: '',
      videoHistory: [],
      videoName: '',
      artistName: ''
    }
    this.updateVal = this.updateVal.bind(this);
  }
  logIt() {
    // replace state with full new object
    let temp = this.state.videoHistory.slice()
    temp.push({id: this.state.videoName, name: this.state.artistName, url: this.state.route})
    this.setState(() =>({
      videoHistory: temp,
      artistName: '',
      videoName: ''
    }))
  }
  updateVal(event){
    let update = event.target.value
    let name = event.target.name;
    this.setState(()=>({
      [name]: update
    }))
  }
  updateVideo(video) {
    this.setState(()=>({
      route: video
    }))
  }
  render(){
  return (<div>
    <h1>Insert your video</h1>
    <AllVideos videos={this.state.videoHistory} vidchange = {this.updateVal.bind(this)}/>
    <label>Video file:</label>
    <input type="url" name="route" value={this.state.lastRoute} onChange={this.updateVal.bind(this)}/>
    <label for="artistname">Artist name</label>
    <input type="text" id="artistname" name="artistName" value={this.state.artistName} onChange={this.updateVal.bind(this)}/>
    <label for="videoname">Video name</label>
    <input type="text" name="videoName" id="videoname" onChange={this.updateVal.bind(this)}/>
    <button onClick={(data) => {this.logIt(data)}}>
      <span>add it</span>
    </button>
    <Player url={this.state.route}/>
  </div>)
  }
}
export default Home
