import React from 'react'
import Player from '../player'
import AllVideos from '../allVideos'

class Home extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      route: '',
      videoHistory: []
    }
  }
  logIt(event) {
    if (!event) {
      return
    }
    let reader = new FileReader()
    let file = event.target.files[0]
    if(file) {
      reader.readAsDataURL(file)
    }
    reader.onloadend = ()=>{
      console.warn(reader.result)
      console.log(file.name)
      // replace state with full new object
      let temp = this.state.videoHistory.slice()
      temp.push({id: file.name, url: reader.result})
      this.setState(() =>({
        route: reader.result,
        videoHistory: temp
      }))
    }
  }
  render(){
  return (<div>
    <h1>Insert your video</h1>
    <AllVideos videos={this.state.videoHistory}/>
    <label>Video file:</label>
    <input type="file"
      onChange ={(data) => {this.logIt(data)}}
      accept=".mp4, .jpeg" />
      <Player url={this.state.route}/>
  </div>)
  }
}
export default Home
