import React from 'react'
import Player from '../player'

class Home extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      route: ''
    }
  }
  logIt(event) {
    if (!event) {
      return
    }
    let reader = new FileReader()
    let file = event.target.files[0]
    if(file) {
      reader.readAsDataURL(file)
    }
    reader.onloadend = ()=>(console.warn(reader.result))
    this.setState({route: reader.result})
  }
  render(){
  return (<div>
    <h1>Insert your video</h1>
    <label>Video file:</label>
    <input type="file"
      onChange ={(data) => {this.logIt(data)}}
      accept=".mp4, .jpeg" />
      <Player url={this.state.route}/>
  </div>)
  }
}
export default Home
