import React from 'react'
import Player from '../player'
import AllVideos from '../allVideos'

class Home extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      route: '',
      videoHistory: [],
      videoName: '',
      artistName: ''
    }
    this.updateurl = this.updateurl.bind(this);
  }
  logIt() {
    // replace state with full new object
    let temp = this.state.videoHistory.slice()
    temp.push({id: this.state.videoName, name: this.state.artistName, url: this.state.route})
    this.setState(() =>({
      videoHistory: temp,
      lastRoute: '',
      videoName: '',
      artistName: ''
    }))
  }
  updateurl(event){
    let url = event.target.value
    this.setState(()=>({
      route: url
    }))
  }
  updateName(event){
    let name = event.target.value
    this.setState(()=>({
      videoName: name
    }))
  }
  updateArtist(event){
    let name = event.target.value
    this.setState(()=>({
      artistName: name
    }))
  }
  updateVideo(video) {
    this.setState(()=>({
      route: video
    }))
  }
  render(){
  return (<div>
    <h1>Insert your video</h1>
    <AllVideos videos={this.state.videoHistory} vidchange = {this.updateVideo.bind(this)}/>
    <label>Video file:</label>
    <input type="url" name="url" value={this.state.lastRoute} onChange={this.updateurl.bind(this)}/>
    <input type="text" name="artist" value={this.state.artistName} onChange={this.updateArtist.bind(this)}/>
    <input type="text" name="videoname" onChange={this.updateName.bind(this)}/>
    <button onClick={(data) => {this.logIt(data)}}>
      <span>add it</span>
    </button>
    <Player url={this.state.route}/>
  </div>)
  }
}
export default Home
