import React from 'react'
import Player from '../player'
import AllVideos from '../allVideos'

class Home extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      route: '',
      videoHistory: [],
      name: ''
    }
    this.updateurl = this.updateurl.bind(this);
  }
  logIt(value) {
    console.warn(value)
    // replace state with full new object
    let temp = this.state.videoHistory.slice()
    temp.push({id: value.name, url: value})
    this.setState(() =>({
      route: value,
      videoHistory: temp
    }))
    event.preventDefault();
  }
  updateurl(url){
    console.log(url)
    this.setState(()=>({
      route: url
    }))
  }
  render(){
  return (<div>
    <h1>Insert your video</h1>
    <AllVideos videos={this.state.videoHistory} vidchange = {this.updateurl}/>
    <form onSubmit={this.logIt}>
    <label>Video file:</label>
    <input type="url" name="url"/>
    <input type="text" name="name"/>
    <input type="submit" value="Submit"/>
    </form>
    <Player url={this.state.route}/>
  </div>)
  }
}
export default Home
