import React from 'react'

const Home = props => {
  function logIt() {
    console.log('fired')
    console.warn(this.fileUpload[0])
  }
  return(
  <div>
    <h1>Insert your video</h1>
    <label>Video file:</label>
    <input type="file"
      ref = {(ref)=> this.fileUpload = ref}
      accept=".mp4, .jpeg" />
    <button onClick={logIt}>
      <span>Submit</span>
    </button>
  </div>)
}
export default Home
