import React from 'react'
import Player from '../player'
import AllVideos from '../allVideos'

class Home extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      route: '',
      videoHistory: [],
      videoName: ''
    }
    this.updateurl = this.updateurl.bind(this);
  }
  logIt(data) {
    console.log(data)
    // replace state with full new object
    let temp = this.state.videoHistory.slice()
    temp.push({id: data.id, url: data.url})
    this.setState(() =>({
      videoHistory: temp
    }))
  }
  updateurl(event){
    let url = event.target.value
    console.log(url)
    this.setState(()=>({
      route: url
    }))
  }
  updateName(event){
    let name = event.target.value
    this.setState(()=>({
      videoName: name
    }))
  }
  render(){
  return (<div>
    <h1>Insert your video</h1>
    <AllVideos videos={this.state.videoHistory} vidchange = {this.updateurl}/>
    <form onSubmit={this.logIt}>
    <label>Video file:</label>
    <input type="url" name="url" onChange={this.updateurl}/>
    <input type="text" name="videoName" onChange={this.updateName}/>
    <button onClick={(data) => {this.logIt(data)}}>
      <span>add it</span>
    </button>
    </form>
    <Player url={this.state.route}/>
  </div>)
  }
}
export default Home
