import React from 'react'

const Home = props => {
  const file = fileUpload.files[0] || null
  function logIt(file) {
    console.log('fired')
    console.warn(file)
  }
  return(
  <div>
    <h1>Insert your video</h1>
    <label>Video file:</label>
    <input type="file"
      ref = {(ref)=> fileUpload = ref}
      accept=".mp4, .jpeg" />
    <button onClick={logIt}>
      <span>Submit</span>
    </button>
  </div>)
}
export default Home
