import React from 'react'
import Player from '../player'

class Home extends React.Component {
  constructor(props){
    super(props)
    this.route = ''
  }
  logIt(event) {
    if (!event) {
      return
    }
    console.warn(event.target.files[0])
    this.props.route = event.target.files[0]
  }
  render(){
  return (<div>
    <h1>Insert your video</h1>
    <label>Video file:</label>
    <input type="file"
      onChange = {this.logIt()}
      accept=".mp4, .jpeg" />
      <Player/>
  </div>)
  }
}
export default Home
