import React from 'react'
import AllVideos from '../allVideos'
import { Player, BigPlayButton } from 'video-react';

class Home extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      route: '',
      videoHistory: [],
      videoName: '',
      artistName: '',
      lastRoute: '',
      ended: false
    }
    this.updateVal = this.updateVal.bind(this);
  }
  logIt() {
    // replace state with full new object
    let temp = this.state.videoHistory.slice()
    temp.push({id: this.state.videoName, name: this.state.artistName, url: this.state.route})
    this.setState(() =>({
      videoHistory: temp,
      artistName: '',
      videoName: '',
      lastRoute:''
    }))
  }
  updateVal(event){
    let update = event.target.value
    let name = event.target.name;
    if(name === 'route'){
      this.setState(()=>({
        lastRoute: update
      }))}
    this.setState(()=>({
      [name]: update
    }))
  }
  updateVideo(video) {
    this.setState(()=>({
      route: video
    }))
  }
  handleStateChange(state) {
    this.setState({player: state, ended: state.ended})
    if(this.state.ended){
      this.continousPlaying()
    }
  }
  computeUrls(){
    if(this.state.videoHistory.length < 1){
      return this.state.route
    }
    let len = this.state.videoHistory.length - 1
    return this.state.videoHistory[len].url
  }
  continousPlaying() {
    let copyPlaylist = this.state.videoHistory.slice()
    if(copyPlaylist.length < 2){
      return
    }
    let urls = copyPlaylist.map((x)=>{return x.url})
    let tracker = urls.indexOf(this.state.route)
    let nextVid
    (tracker + 1) < copyPlaylist.length ? nextVid = tracker + 1 : nextVid = 0
    this.setState({route: copyPlaylist[nextVid].url, ended: false})
    this.refs.player.play()
  }
  componentDidMount() {
    // suscribe to player state changes
    this.refs.player.subscribeToStateChange(this.handleStateChange.bind(this))
  }
  render(){
  return (<div>
    <h1>Add videos to your playlist</h1>
    <label>Video file:</label>
    <input type="url" name="route" value={this.state.lastRoute} onChange={this.updateVal.bind(this)}/>
    <label htmlFor="artistname">Artist name</label>
    <input type="text" id="artistname" name="artistName" value={this.state.artistName} onChange={this.updateVal.bind(this)}/>
    <label htmlFor="videoname">Video name</label>
    <input type="text" name="videoName" id="videoname" value={this.state.videoName} onChange={this.updateVal.bind(this)}/>
    <button onClick={() => {this.logIt()}}>
      <span>add it</span>
    </button>
    <div className="wrapper">
      <div className="player">
        <Player
          ref="player"
          playsInline
          autoPlay
          src={this.state.route}>
          <BigPlayButton position="center" />
        </Player>
      </div>
      <div className="playlist">
        <AllVideos videos={this.state.videoHistory} vidchange = {this.updateVideo.bind(this)}/>
      </div>
    </div>
  </div>)
  }
}
export default Home
