import React from 'react'
import Player from '../player'
import AllVideos from '../allVideos'

class Home extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      route: '',
      videoHistory: []
    }
    this.updateurl = this.updateurl.bind(this);
  }
  logIt(event) {
    if (!event) {
      return
    }
    let file = event.target.files[0]
    console.warn(file)
    // replace state with full new object
    let temp = this.state.videoHistory.slice()
    temp.push({id: file.name, url: file})
    this.setState(() =>({
      route: reader.result,
      videoHistory: temp
    }))
  }
  updateurl(url){
    console.log(url)
    this.setState(()=>({
      route: url
    }))
  }
  render(){
  return (<div>
    <h1>Insert your video</h1>
    <AllVideos videos={this.state.videoHistory} vidchange = {this.updateurl}/>
    <label>Video file:</label>
    <input type="url"
      accept=".mp4, .jpeg" />
      <button onClick ={(data) => {this.logIt(data)}}></button>
      <Player url={this.state.route}/>
  </div>)
  }
}
export default Home
